document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".center-icon").forEach(e=>{let t=e.parentElement.querySelector(".after-content"),n,c=!1;e.addEventListener("mouseenter",function(){c||(clearTimeout(n),t.classList.add("active"))}),e.addEventListener("mouseleave",function(){c||(n=setTimeout(()=>{t.classList.remove("active")},100))}),e.addEventListener("click",function(){(c=!c)?t.classList.add("active"):t.classList.remove("active")}),t.addEventListener("mouseenter",function(){c||(clearTimeout(n),t.classList.add("active"))}),t.addEventListener("mouseleave",function(){c||t.classList.remove("active")})})});